import React, { useEffect, useState, useCallback } from "react";
import axios from 'axios';
import MovieItem from "./MovieItem";

const MovieList = () => {
    const [articles, setArticles] = useState(null);
    const [loading, setLoading] = useState(false);

    const [search, setSearch] = useState('');

    const onChange = useCallback(e => {
        setSearch(e.target.value);
        console.log(e.target.value);
    },[]);

    const onClick = useCallback(e => {
        console.log(e.target.value);
        setSearch('');
    },[]);

    const onKeyPress = (e) => {
        if (e.key === 'Enter') {
        onClick();
        }
    };


    const NAVER_CLIENT_ID = "NOt2yyw4Gn6Y5gvyBB8T";
    const NAVER_CLIENT_SECRET = "unzLll0HB9";

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);

            try {
                const response = await axios.get("/v1/search/movie", {
                    params: {
                        // query: "마블"
                        query: {search},
                    },
                    headers: {
                        "X-Naver-Client-Id": NAVER_CLIENT_ID,
                        "X-Naver-Client-Secret": NAVER_CLIENT_SECRET,
                    },
                });
                setArticles(response.data.items);
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        };
        fetchData();
    },[]);

    if(loading) {
        return <p>불러오는중</p>;
    }

    if(!articles) {
        return null;
    }

    return (
        <div>
            <input type="text" name="search" value={search} onChange={onChange} onKeyPress={onKeyPress} />
            <button onClick={onClick}>검색</button>
            {articles.map(article => (<MovieItem key={article.link} article={article} />))}
        </div>
    );
};

export default MovieList;
