import './App.css';
import React, {useState, useCallback} from 'react';
import axios from 'axios';

const App = () => {
  const [data, setData] = useState(null);

  const NAVER_CLIENT_ID = "NOt2yyw4Gn6Y5gvyBB8T";
  const NAVER_CLIENT_SECRET = "NOt2yyw4Gn6Y5gvyBB8T";

  const onClick = () => {
    axios
      // url로 접근시 console창에 Access to XMLHttpRequest... 와 같은 오류 발생
      // -> package.json에서 proxy 추가
      .get("https://openapi.naver.com/v1/search/blog",{
        params: {
          query: "react",
          start: 1,
          display: 5,
          sort: "sim",
        },
        headers: {
          "X-Naver-Client-Id": NAVER_CLIENT_ID,
          "X-Naver-Client-Secret": NAVER_CLIENT_SECRET,
        },
      })
      .then((response) => {
        setData(response.data);
      });
  };

  return (
    <div>
      <div>
        <button onClick={onClick}> 불러오기 </button>
      </div>
      {data && (
        <textarea rows={7} value={JSON.stringify(data, null, 2)} readOnly={true} />
      )}
    </div>
  );
};

export default App;
