import { useCallback, useState } from 'react';
import axios from 'axios';
import MovieList from './MovieList';
import '../css/Search.scss';

const Search = () => {    
    const [search, setSearch] = useState('');
    const [articles, setArticles] = useState(null);
    const [loading, setLoading] = useState(false);
    const [category, setCategory] = useState('');

    const onChange = useCallback(e => {
        setSearch(e.target.value);
        // console.log(e.target.value);
    },[]);

    const ChangeCategory = e => {
        setCategory(e.target.value);
    };

    const onClick = useCallback( e => {
        const NAVER_CLIENT_ID = "NOt2yyw4Gn6Y5gvyBB8T";
        const NAVER_CLIENT_SECRET = "unzLll0HB9";

        
        const fetchData = async () => {
            setLoading(true);

            try {
                const response = await axios.get(`/v1/search/movie?query=${search}`, {
                // const response = await axios.get(`/v1/search/movie`, {
                    // params: {
                    //     // query: "마블"
                    //     // query: {search},
                    // },
                    headers: {
                        "X-Naver-Client-Id": NAVER_CLIENT_ID,
                        "X-Naver-Client-Secret": NAVER_CLIENT_SECRET,
                    },
                });
                setArticles(response.data.items);
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        };
        fetchData();

        if(loading) {
            return <p>불러오는중</p>;
        }

        if(!articles) {
            return null;
        }

        setSearch('');
    },[search]);

    const onKeyPress = (e) => {
        if (e.key === 'Enter') {
        onClick();
        }
    };

    console.log(search);

  return (
    <div className="Search">
        <button value="blog" onClick={ChangeCategory}>blog</button>
        <button value="movie" onClick={ChangeCategory}>movie</button>


        <input type="text" name="search" value={search} onChange={onChange} onKeyPress={onKeyPress} />
        <button onClick={onClick}>검색</button>
        {/* {articles.map(article => (<MovieList key={article.link} article={article} />))} */}
        <MovieList articles={articles} />
    </div>
  );

};

export default Search;
